import { defineVxeComponent } from '../../ui/src/comp';
import XEUtils from 'xe-utils';
import { getIcon, createEvent, renderEmptyElement } from '../../ui';
export default {
    name: 'VxeResult',
    props: {
        imageUrl: String,
        imageStyle: Object,
        icon: String,
        type: [String, Number],
        status: [String, Number],
        title: [String, Number],
        content: [String, Number]
    },
    data() {
        const xID = XEUtils.uniqueId();
        const reactData = {};
        return {
            xID,
            reactData
        };
    },
    methods: {
        //
        // Method
        //
        dispatchEvent(type, params, evnt) {
            const $xeResult = this;
            $xeResult.$emit(type, createEvent(evnt, { $result: $xeResult }, params));
        },
        //
        // Render
        //
        renderVN(h) {
            const $xeResult = this;
            const props = $xeResult;
            const slots = $xeResult.$scopedSlots;
            const { imageUrl, imageStyle, icon, title, type, content } = props;
            const status = props.status || type;
            const slotExtra = slots.extra;
            return h('div', {
                ref: 'refElem',
                class: ['vxe-result', {
                        [`theme--${status}`]: status
                    }]
            }, [
                h('div', {
                    class: 'vxe-result--inner'
                }, [
                    imageUrl
                        ? h('div', {
                            class: 'vxe-result--img-wrapper'
                        }, [
                            h('img', {
                                attrs: {
                                    src: imageUrl
                                },
                                style: imageStyle
                            })
                        ])
                        : h('div', {
                            class: 'vxe-result--icon-wrapper'
                        }, [
                            h('i', {
                                class: [icon, type ? getIcon()[`RESULT_${type}`.toLocaleUpperCase()] : '']
                            })
                        ]),
                    h('div', {
                        class: 'vxe-result--title-wrapper'
                    }, `${title || ''}`),
                    h('div', {
                        class: 'vxe-result--content-wrapper'
                    }, `${content || ''}`),
                    slotExtra
                        ? h('div', {
                            class: 'vxe-result--extra-wrapper'
                        }, slotExtra({}))
                        : renderEmptyElement($xeResult)
                ])
            ]);
        }
    },
    render(h) {
        return this.renderVN(h);
    }
};
